import { useDispatch, useSelector } from "react-redux";
import ProductsList from "../components/Products/ProductList/ProductsList";
import { cartActions } from "../store/product-slice";
import { useEffect, useState } from "react";
import { loadProducts } from "../store/cart-actions";

const ProductsPage = () => {
  const [meals, setMeals] = useState({});



  const dispatch = useDispatch();
  const products = useSelector((state) => state.products.products);

  async function load() {
    const response = await fetch(
      "www.themealdb.com/api/json/v1/1/categories.php"
    );
    const data = await response.json();
    console.log(data);
    setMeals(data)
  }
  console.log(meals);;
  useEffect(() => {
    const interval = setTimeout(() => {
      dispatch(cartActions.resetProduct());
      dispatch(loadProducts());
    }, 500);

    return () => {
      clearInterval(interval);
    };
  }, [dispatch]);

  return (
    <>
    <button onClick={load}>sadasddas</button>
      {products.map((product) => (
        <ProductsList
          key={product.id}
          title={product.title}
          price={product.price}
          totalPrice={product.totalPrice}
          id={product.id}
        />
      ))}
    </>
  );
};

export default ProductsPage;

