import { useDispatch, useSelector } from "react-redux";
import ProductsList from "../components/Products/ProductList/ProductsList";
import { cartActions } from "../store/product-slice";
import { useEffect } from "react";
import { loadProducts } from "../store/cart-actions";
import { json, useLoaderData } from "react-router-dom";

const ProductsPage = () => {
  const data = useLoaderData();
  console.log(data);
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products.products);
  
  useEffect(() => {
  const interval = setTimeout(() => {
      dispatch(cartActions.resetProduct());
      dispatch(loadProducts());
    }, 500)
   async function load() {
      const response = await fetch("www.themealdb.com/api/json/v1/1/categories.php");
      if (!response.ok) {
        return json({ message: "Could not fetch any events" }, { status: 500 });
      } else {
        console.log(response.categories);
        return await JSON.stringify(response);
      }
    }
    load()
    return () => {
      clearInterval(interval);
    }
  }, [dispatch])

  return (
    <>
      
      {products.map((product) => (
        <ProductsList
          key={product.id}
          title={product.title}
          price={product.price}
          totalPrice={product.totalPrice}
          id={product.id}
        />
      ))}
    </>
  );
};

export default ProductsPage;

export async function loader() {
  const response = await fetch("www.themealdb.com/api/json/v1/1/list.php?c=list");
  if (!response.ok) {
    return json({ message: "Could not fetch any events" }, { status: 500 });
  } else {
    return await response;
  }
}
