import { useDispatch, useSelector } from "react-redux";
import ProductsList from "../components/Products/ProductList/ProductsList";
import { cartActions } from "../store/product-slice";
import { useEffect, useState } from "react";
import { loadProducts } from "../store/cart-actions";

const ProductsPage = () => {
  const [meals, setMeals] = useState(null);

  const dispatch = useDispatch();
  const products = useSelector((state) => state.products.products);

  useEffect(() => {
    const fetchMeals = async () => {
     const response = await fetch("www.themealdb.com/api/json/v1/1/categories.php")
       const json = await response.json();

       if (response.ok) {
        setMeals(json)
       }
       console.log(json);
    } 
    fetchMeals();
  }, [])
  useEffect(() => {
    const interval = setTimeout(() => {
      dispatch(cartActions.resetProduct());
      dispatch(loadProducts());
    }, 500);

    return () => {
      clearInterval(interval);
    };
  }, [dispatch]);

  return (
    <>
    
      {products.map((product) => (
        <ProductsList
          key={product.id}
          title={product.title}
          price={product.price}
          totalPrice={product.totalPrice}
          id={product.id}
        />
      ))}
    </>
  );
};

export default ProductsPage;

