import { useNavigate } from 'react-router-dom';

import classes from './ProductForm.module.css';

function ProductForm({ method, event }) {
  const navigate = useNavigate();
  function cancelHandler() {
    navigate('..');
  }

  return (
    <form className={classes.form}>
      <p>
        <label htmlFor="title">Title</label>
        <input id="title" type="text" name="title" required defaultValue={event ? event.title : ''}/>
      </p>
      <p>
        <label htmlFor="price">Image</label>
        <input id="price" type="number" name="price" required defaultValue={event ? event.price : ''}/>
      </p>
      <p>
        <label htmlFor="totalPrice">Total Price</label>
        <input id="totalPrice" type="number" name="totalPrice" required defaultValue={event ? event.totalPrice : ''} />
      </p>
      <p>
        <label htmlFor="quantity">Quantity</label>
        <input id="quantity" type="number" name="quantity" defaultValue={event ? event.quantity : ''} required />
      </p>

      <div className={classes.actions}>
        <button type="button" onClick={cancelHandler}>
          Cancel
        </button>
        <button>Save</button>
      </div>
    </form>
  );
}

export default ProductForm;
